(function(){
	"use strict";

	var system = require("system");
	var fs = require( "fs" );

	phantom.onError = function(msg, trace) {
		var msgStack = ['PHANTOM ERROR: ' + msg];
		if (trace && trace.length) {
			msgStack.push('TRACE:');
			trace.forEach(function(t) {
				msgStack.push(' -> ' + (t.file || t.sourceURL) + ': ' + t.line + (t.function ? ' (in function ' + t.function +')' : ''));
			});
		}
		system.stderr.write( msgStack.join('\n') );
		phantom.exit(1);
	};

	var RSVP = require('./rsvp');
	var processor = require('./processor');

	RSVP.on('error', function(reason) {
		throw new Error( reason );
	});

	// alternate syntax for PhantomJS 2.x (system.args) / 1.x (phantom.args)
	var options = (typeof(phantom.args) === "undefined") ? {
		inputFiles: JSON.parse(system.args[1]),
		dest: system.args[2],
		defaultWidth: system.args[3],
		defaultHeight: system.args[4]
	} : {
		inputFiles: JSON.parse(phantom.args[0]),
		dest: phantom.args[1],
		defaultWidth: phantom.args[2],
		defaultHeight: phantom.args[3]
	};

	if( !options.dest.match( fs.separator + '$' ) ){
		options.dest += fs.separator;
	}


	var promises = options.inputFiles.map( function( file ){
		return processor.processFile( file, options );
	});


	RSVP.all( promises )
	.then( function(){
		phantom.exit();
	})
	.catch(function(err){
		throw new Error( err );
	});
})();

/* Runs in phantom */
/*global module:true*/
/*global require:true*/
(function(){
	"use strict";

	var fs = require( 'fs' );


	var Gfile = function( filename ){
		var svgRegex = /\.svg$/i,
			fArr = filename.split( fs.separator );
		this.filename = fArr.pop();
		this.pathdir = fArr.join( fs.separator );
		this.uri = "file:///" + fArr.join( "/" ) + "/" + this.filename;
		if( !fs.exists( this.pathdir + fs.separator + this.filename ) ){
			throw new Error( this.pathdir + fs.separator + this.filename + " does not exist" );
		}
		this.filenamenoext = this.filename.replace( svgRegex , "" );
	};

	Gfile.prototype.setImageData = function(){
		this.imagedata = fs.read( this.pathdir + fs.separator + this.filename ) || "";
	};


	module.exports = Gfile;

}());

